import React, { Component } from 'react';
import { FormGroup, ControlLabel, FormControl, HelpBlock, Tabs,Tab}  from 'react-bootstrap';


class DisplayQuantitiesAndTimes extends React.Component {
  constructor(props, context) {
    super(props, context);



    this.handleSelect = this.handleSelect.bind(this);

      this.state = {
        key: 1
      };
    }

    handleSelect(key) {
      alert(`selected ${key}`);
      this.setState({ key });
    }

    render() {
      return (
        <div>
        <h2>Baking Time</h2>

        <Tabs
          activeKey={this.state.key}
          onSelect={this.handleSelect}
          id="controlled-tab-example"
        >
          <Tab eventKey={1} title="In Room<">
            Approx {(Math.round(this.props.baketime/60000))} for {(this.props.breadType)} of this size
              Time of room rise is:  {(this.props.proofingtimes.inroom)} mins.(that is about {(Math.round(this.props.proofingtimes.inroom/6)/10)} hours )<br/>
              Start at {(this.props.roomstart)}, with a shape around {(this.props.roomshape)} to achive the desired time in the room<br/>
          </Tab>
          <Tab eventKey={2} title="In Fridge">          
              Time of fridge rise is:  {(this.props.proofingtimes.infridge)} mins. (that is about {(Math.round(this. props.proofingtimes.infridge/6)/10)} hours )<br/>
              Start at {(this.props.fridgestart)}, with a shape around {(this.props.fridgeshape)} to achive the desired time in the fridge<br/>
          </Tab>
          <Tab eventKey={3} title="Fridge, then Room ">          
            Start at {(this.props.roomplusfridgestart)}, with a shape around {(this.props.roomplusfridgeshape)} <br/>
            </Tab>
          <Tab eventKey={4} title="Room, then Fridge " >          
            <h2> </h2>
            Start at {(this.props.fridgeplusroomstart)}, with a shape around {(this.props.fridgeplusroomshape)} <br/>


          </Tab>
        </Tabs>
                      Mass of starter is {(this.props.startermass)} g
                      <DoughHydration doughmass={(this.props.doughmass)} startermass={(this.props.startermass)} />
        </div>
      );
    }
  }

  export default DisplayQuantitiesAndTimes;
