import React, { Component } from 'react';

class DisplayQuantitiesAndTimes extends React.Component {
  constructor(props, context) {
    super(props, context);

  }
    render() {


      return (
        <div>
            Approx {(Math.round(this.props.baketime/60000))} for {(this.props.breadType)} minutes of this size <br/>
              Time of {(this.props.location[0])} rise is:  {(this.props.time[0])} mins.(that is about {(Math.round(this.props.time[0]/6)/10)} hours )<br/>
              {this.props.location>1? 
            <div>Time of {(this.props.location[0])} rise is:  {(this.props.time[0])} mins.(that is about {(Math.round(this.props.time[0]/6)/10)} hours )<br/ ></div> : <span/> }
              Start at {(this.props.interfere[0])}  
              {this.props.time.length>1 ? <span> , then {(this.props.interfere[1])}  in second location  </span> :<span> </span> }           
               with a shape around {(this.props.interfere[1])} to achive the desired time in the {(this.props.title)}<br/>
        </div>
      );
    }
  }

  export default DisplayQuantitiesAndTimes;
