import React, { Component } from 'react';
import { FormGroup, ControlLabel, FormControl, HelpBlock, Tabs,Tab}  from 'react-bootstrap';
import  DoughHydration from '../doughHydration/doughHydration';
import DisplayQuantitiesAndTimes from './../displayQuantitiesAndTimes/displayQuantititesAndTimes'; 
import QuantitiesAndTimes from '../quantitiesAndTimes/quantitiesAndTimes';

class DisplayQuantitiesTimesTab extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.handleSelect = this.handleSelect.bind(this);

      this.state = {
        key: 1
      };
    }

    handleSelect(key) {
      this.setState({ key });
    }

    render() {

    const theDoughProps = this.props.doughProps;
    const nooftemps = 1;
    var start = 0;
    var place = 0;
    var shape = null;
    var breadType = null;
    var title = '';
    var interfere = [];
    var baketime = 0 ;
    var time = [];

    switch (this.state.key) {
      case 4 :
      start=this.props.fridgeplusroomstart;
      title = 'Room, then Fridge';
      baketime = [this.props.baketime];
      time = [this.props.inroom,this.props.infridge];
      interfere = [this.props.fridgeplusroomstart,this.props.fridgeplusroomshape];
      breadType = this.props.breadType;


      break;
      case 1:
      default:
        start=this.props.roomstart;
        title = 'In Room';
        baketime = [this.props.baketime];
        time = [this.props.inroom];
        interfere = [this.props.roomstart,this.props.roomshape];
        breadType = this.props.breadType;
    }
      return (
        <div>
        <h2>Rise and Baking Time</h2>
        <Tabs
          activeKey={this.state.key}
          onSelect={this.handleSelect}
          id="controlled-tab-example"
        >
          <Tab eventKey={1} title="In Room">
            <DisplayQuantitiesAndTimes start={start} title={title} baketime={baketime} time={time} interfere={interfere} breadType={breadType} />
          </Tab>
          <Tab eventKey={2} title="In Fridge">          
              Time of fridge rise is:  {(this.props.infridge)} mins. (that is about {(Math.round(this. props.infridge/6)/10)} hours )<br/>
              Start at {(this.props.fridgestart)}, with a shape around {(this.props.fridgeshape)} to achive the desired time in the fridge<br/>
          </Tab>
          <Tab eventKey={3} title="Fridge, then Room ">          
            Start at {(this.props.roomplusfridgestart)}, with a shape around {(this.props.roomplusfridgeshape)} <br/>
            </Tab>
          <Tab eventKey={4} title="Room, then Fridge " >          
            <DisplayQuantitiesAndTimes start={start} title={title} baketime={baketime} time={time} interfere={interfere} breadType={breadType} />


          </Tab>
        </Tabs>
                      Mass of starter is {(this.props.startermass)} g
                      <DoughHydration doughmass={(this.props.doughmass)} startermass={(this.props.startermass)} />

               </div>
      );
    }
  }

  export default DisplayQuantitiesTimesTab;
